function count_until(value, n, words) {
  var count = 0;
  var occurrences = 0;
  for (var i = 0; i < words.length; i++) {
    var word = words[i];
    if (word != value)
      count += 1;
    else
      occurrences += 1;
      if (occurrences === n)
        break;
  }
  return count;
}

