function reverse_sentence_words(s) {
  var word_array = s.split();
  for (var i = 0; i < word_array.length; i++) {
    var word = word_array[i];
    var reverse_word = '';
    for (var j = word.length - 1; j >= 0; j--) {
      reverse_word += word[j];
    }
    word_array[i] = reverse_word;
  }
  return word_array.join(' ');
}

