function get_time_str(s) {
  var hours = s / 3600;
  var minutes = (s - (3600 * hours)) / 60;
  var seconds = s - (3600 * hours) - (60 * minutes);
  return hours + ' hours, ' + minutes + ' minutes, ' + seconds + ' seconds';
}

